import { useState, useEffect } from 'react'

function Cadastro() {
    const [input, setInput] = useState('')
    const [tarefas, setTarefas] = useState([
        "Pagar conta de luz",
        "Estudar programação",
        "Enviar a tarefa"
    ])
    const [namesUser, setNamesUser] = useState()

    const tarefasStorage = localStorage.getItem('@tarefa')
    const namesStorage = localStorage.getItem('@namesUser')

    useEffect(() => {
        const nameUser = prompt('Digite seu nome:')

        if (tarefasStorage) setTarefas(JSON.parse(tarefasStorage))
        if (tarefasStorage) setNamesUser(namesStorage)

    }, [tarefas])

    function handleRegistro(e) {
        e.preventDefault()

        setTarefas([...tarefas, input])
        setInput('')
    }




    useEffect(() => {

        if (namesUserStorage) setNamesUser(JSON.parse(namesUserStorage))
    }, [namesUser])

    function handleRegistro(e) {
        e.preventDefault()

        setNamesUser([...namesUser, input])
        setInput('')
    }


    return (
        <div>

        </div>
    );
}
export default Cadastro
