import { useState, useEffect } from 'react'

function Cadastro() {
    const [input, setInput] = useState('')
    const [tarefas, setTarefas] = useState([
        "Pagar conta de luz",
        "Estudar programação",
        "Enviar a tarefa"
    ])
    const [namesUser, setNamesUser] = useState([])
    const [selectedColor, setSelectedColor] = useState('white')

    const tarefasStorage = localStorage.getItem('@tarefa')
    const nameUser = localStorage.getItem('nome')
    let name

    useEffect(() => {

        if (tarefasStorage) {
            setTarefas(JSON.parse(tarefasStorage))
        }
        if (nameUser != '') {
            name = prompt('Digite seu nome:')
            setNamesUser(name)
        }


    }, [])

    useEffect(() => {
        localStorage.setItem('@tarefa', JSON.stringify(tarefas))
        localStorage.setItem('nome', JSON.stringify(namesUser))

    }, [tarefas, namesUser])


    function handleRegistro(e) {
        e.preventDefault()
        setTarefas([...tarefas, input])
        setInput('')
    }

    const handleColorChange = (color) => {
        setSelectedColor(color);
    };

    return (
        <div>
            <h1>Olá, {localStorage.getItem('nome')}! Sua lista de tarefas:</h1>

            <form onSubmit={handleRegistro}>
                <label>Nome da Tarefa: </label><br />
                <input placeholder='Digite uma Tarefa'
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                /><br />

                <button type='submit'>Registro</button>
            </form>
            <br /><br />

            <ul>
                {tarefas.map(tarefa => (
                    <li key={tarefa}>{tarefa}</li>
                ))}
            </ul>

            <div>
                <label>
                    <input
                        type="radio"
                        value="white"
                        checked={selectedColor === "white"}
                        onChange={() => handleColorChange("white")}
                    />
                    Branco
                </label>
                <label>
                    <input
                        type="radio"
                        value="lightblue"
                        checked={selectedColor === "lightblue"}
                        onChange={() => handleColorChange("lightblue")}
                    />
                    Azul Claro
                </label>
                <label>
                    <input
                        type="radio"
                        value="lightgreen"
                        checked={selectedColor === "lightgreen"}
                        onChange={() => handleColorChange("lightgreen")}
                    />
                    Verde Claro
                </label>
            </div>
            <br /><br />
        </div>
    );
}
export default Cadastro
