import { useState, useEffect } from 'react'

function Cadastro() {
    const [input, setInput] = useState('')
    const [tarefas, setTarefas] = useState([
        "Pagar conta de luz",
        "Estudar programação",
        "Enviar a tarefa"
    ])
    const [namesUser, setNamesUser] = useState('')

    const tarefasStorage = localStorage.getItem('@tarefa')
    const namesStorage = localStorage.getItem('@names')

    useEffect(() => {
        let count = 0
        if (count == 0) {
            count += 1
            const nameUser = prompt('Digite seu nome:')
        }

        if (tarefasStorage) setTarefas(JSON.parse(tarefasStorage))


        setNamesUser(JSON.parse(namesStorage))

    }, [namesUser])

    function handleRegistro(e) {
        e.preventDefault()
        setTarefas([...tarefas, input])
        setInput('')
    }

    return (
        <div>
            <h1>Cadastro de Tarefas</h1>

            <form onSubmit={handleRegistro}>
                <label>Nome da Tarefa: </label><br />
                <input placeholder='Digite uma Tarefa'
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                /><br />

                <button type='submit'>Registro</button>
            </form>
            <br /><br />

            <ul>
                {tarefas.map(tarefa => (
                    <li key={tarefa}>{tarefa}</li>
                ))}
            </ul>
        </div>
    );
}
export default Cadastro
