import { useState, useEffect } from 'react'

function Cadastro() {
    const [input, setInput] = useState('')
    const [namesUser, setNamesUser] = useState([
        "Pagar conta de luz",
        "Estudar programação",
        "Enviar a tarefa"
    ])


    useEffect(() => {
        const nameUser = prompt('Digite seu nome:')
        if (namesUserStorage) setNamesUser(JSON.parse(namesUserStorage))
    }, [namesUser])

    function handleRegistro(e) {
        e.preventDefault()

        setNamesUser([...namesUser, input])
        setInput('')
    }


    return (
        <div>

        </div>
    );
}
export default Cadastro
